var Faker = require('Faker'),
    util = require('util'),
    HTML = require('./index');

var suite = [], start = null;

util.log('Generating suite');

for( var i = 0; i < 999*10; i++ ){
    suite.push({
        tagName: 'li',
        text: Faker.Lorem.sentence(),
        attributes: {
            class: 'test',
            id: 'foobar'
        },
        children: [
            { tagName: 'p', text: Faker.Lorem.sentence() },
            { tagName: 'a', text: Faker.Lorem.sentence(), attributes: { href: 'foobar'  } }
        ]
    });
}


//console.log( suite );

function time( suite ){
    var html = '';

    for( var i = 0; i < suite.length; i++ ){
        html = html+= HTML.element( suite[i] );
    }
    return html;
}

util.log('Start rendering');

start = Date.now();
var out = time( suite );

//console.log( out );
util.log( Date.now()/1000 -  start/1000 );

var thing = HTML.tag('div', 'test', { children: [
    {
        text: 'nexted 1',
        children: [
            {
                text: 'nested 2',
                children: [
                    {
                        html: '<p>with multline\nstrings</p>'
                    }
                ]
            }
        ]
    }
]});

console.log( thing );



